mapper.select:
SELECT user_id, password, email, tell, reg_date FROM tb_member
WHERE user_id = #{id}
AND password = #{pw}
AND is_leave = 0

mapper.update:
UPDATE tb_member
SET password = #{password}, email = #{email}, tell = #{tell}
WHERE user_id = #{userId}

UPDATE tb_member
SET is_leave = 1
WHERE user_id = #{userId}
